'use strict';

var path = require('path');
var launchparams = require('./launchparams');

var serverConfig = {
    port: 0, // Port to start server on.  If 0, port is randomly assigned.
    logDir: null, // Log directory.  If null, logger output goes to stdout
    logLevel: 'info' // Log level
};

// If a sessionID is passed in, we are being started by the appserver.
if (launchparams.hasOwnProperty('sessionID')) {
    serverConfig.port = 0;
    serverConfig.logLevel = 'info';
} else {
    serverConfig.port = launchparams.port || 3000;
    serverConfig.logLevel = 'debug';
}

module.exports = serverConfig;
